SQLStoredProcedureSpec.CreateProcedureSQL:
create procedure dbo.USR_USP_CONSTITUENT_GETPHOTOS ( @IDSETREGISTERID uniqueidentifier = null ) as set nocount on;
-- For more complex branching, write in dynamic SQL
if @IDSETREGISTERID is null select CONSTITUENT.ID, CONSTITUENT.PICTURE from dbo.CONSTITUENT; else select CONSTITUENT.ID, CONSTITUENT.PICTURE from dbo.CONSTITUENT inner join dbo.UFN_IDSETREADER_GETRESULTS_GUID(@IDSETREGISTERID) IDS on IDS.ID = CONSTITUENT.ID;
